import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as SRD from "storm-react-diagrams";
import "./styles.css";
import Modal from 'react-responsive-modal';
import axios from 'axios';
import {Checkbox, CheckboxGroup} from 'react-checkbox-group';

require("storm-react-diagrams/dist/style.min.css");

var engine = new SRD.DiagramEngine();
engine.installDefaultFactories();

// 2) setup the diagram model
var model = new SRD.DiagramModel();

// 3) create a default node
//var node1 = new SRD.DefaultNodeModel("Node 1", "rgb(0,192,255)");
//let port1 = node1.addOutPort("Out");
//node1.setPosition(100, 100);

// 4) create another default node
//var node2 = new SRD.DefaultNodeModel("Node 2", "rgb(192,255,0)");
//let port2 = node2.addInPort("In");
//node2.setPosition(400, 100);

// 5) link the ports
//let link1 = port1.link(port2);

// 6) add the models to the root graph
//model.addAll(node1, node2, link1);

// 7) load model into engine
engine.setDiagramModel(model);
console.log(model);
console.log(engine);

const str="aze,dazad,dscwxc,fdsfsd"


class App extends Component {

 path="EA_result_1351.xlsx";


  fileInput = e => {
    const file = e.target.files[0];
    const newNode = new SRD.DefaultNodeModel(file.name, "blue");
    newNode.addOutPort("out");
    model.addNode(newNode);
    console.log(newNode.name);
    this.forceUpdate();





    /*  this.componentDidMount()
    {
      fetch('http://localhost:9000/findallcolumns/{file.name}').then(res=>{
        res.json()
      }).then(res=>{
        this.setState({
          columns: res
        }).catch(error=>{
          console.log(error)
        })
      })    
    }
  */

  };



  componentDidMount() {
    axios.get(`http://localhost:9000/test3/{path}`)
      .then(res => {
        const s = res.data;
console.log(s);      })
  }





  addSelect = () => {
    const newNode1 = new SRD.DefaultNodeModel("Select", "green");
    newNode1.addOutPort("out");
    newNode1.addInPort("in");
    model.addNode(newNode1);
    console.log(newNode1)
    this.forceUpdate(); 
     console.log(model);

    //  newNode1.addListener({selectionChanged : console.log("******")});


    console.log("hiiii");
    console.log(model.getSelectedItems());





    newNode1.addListener({
      selectionChanged: () => {
        document.getElementById('configure').style.display = 'block';
      }
    });
  };

  addCombine = () => {
    const newNode = new SRD.DefaultNodeModel("Combine", "red");
    newNode.addOutPort("out");
    newNode.addInPort("in1");
    newNode.addInPort("in2");
    model.addNode(newNode);
    this.forceUpdate();
    console.log(model);
  };
  addGroupBy = () => {
    const newNode = new SRD.DefaultNodeModel("GroupBy", "pink");
    newNode.addOutPort("out");
    newNode.addInPort("in");
    model.addNode(newNode);



    this.forceUpdate();
  };

  state = {
    open: false,
  };


  getColumns=()=> {
    var newstr = str.split(',');
    return newstr
  }



  onOpenModal = () => {
    this.setState({ open: true });
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };




  columnNamesChanged = (newIteS) => {
    this.setState({
      // fruits: newFruits
    });
  }


  render() {
    const { open } = this.state;
    return (
      <div className="App">
        <Modal open={open} onClose={this.onCloseModal} center>
          <h2>Simple centered modal</h2>

          <form>
    <CheckboxGroup
      checkboxDepth={2} // This is needed to optimize the checkbox group
      name="fruits"
      value={this.state.columnNames}
      onChange={this.columnNamesChanged}>
  {this.getColumns().map(function(item, i){
    return <label><Checkbox value={item}/> {item}</label>})}
    </CheckboxGroup>
              </form>

        </Modal>        

        <button id="configure" hidden="hidden" onClick={this.onOpenModal} >Configure</button>
        <input type="file" label="upload" onChange={this.fileInput} />
        <button onClick={this.addSelect}>Select</button>
        <button onClick={this.addCombine}>Combine</button>
        <button onClick={this.addGroupBy}>GroupBy</button>

        <SRD.DiagramWidget diagramEngine={engine} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
